const items = $input.all();
const parsedItems = items.map((item) => {
  let text = item?.json?.text || '';
  // Remove leading/trailing code fences (```json or ```), possibly with or without "json"
  text = text.replace(/^```json\s*/i, '').replace(/```$/g, '').trim();
  try {
    const parsedText = JSON.parse(text);
    return { json: parsedText };
  } catch (e) {
    // If parsing fails, pass the original text and error message
    return { json: { originalText: item?.json?.text, error: e.message } };
  }
});
return parsedItems;

